    onBtnAddClick: function(button, e, eOpts) {
        var form = button.up('products');
        var grdProducts = FindControl(form, 'grdProducts');
        var record = Ext.create('SIS.model.ProductModel');

        var win = Ext.create('SIS.view.AddProduct', {
            formModel: record,
            animateTarget: button,
            save: function(model){
                var data = ModelToJson(model);
                Request(
                    '/Home/SaveProduct',
                    data,
                    function(response)
                    {
                        grdProducts.getStore().load();
                    }
                );
            }
        });

        win.setRecord();
        win.show();
    },

    onBtnEditClick: function(button, e, eOpts) {
        var form = button.up('products');
        var grdProducts = FindControl(form, 'grdProducts');
        var record = grdProducts.getSelectedRecord();

        if(IsNullOrEmpty(record))
        {
            Ext.MessageBox.show({
                title: 'საქონლის რედაქტირება',
                msg: '<center>საქონელი არ არის მონიშნული</center>',
                width:300,
                iconCls: 'icon-edit',
                buttons: Ext.MessageBox.OK,
                animateTarget: button
            });
        }
        else
        {
            var win = Ext.create('SIS.view.AddProduct', {
                formModel: record,
                iconCls: 'icon-edit',
                save: function(model){
                    var data = ModelToJson(model);
                    Request(
                        '/Home/SaveProduct',
                        data,
                        function(response)
                        {
                            grdProducts.getStore().load();
                        }
                    );
                },
                animateTarget: button
            });

            win.setTitle('საქონლის რედაქტირება');
            win.setRecord();
            win.show();
        }
    },

    onTxtNameChange: function(field, newValue, oldValue, eOpts) {
        var form = this;

        var btnClear = FindControl(form, 'btnClear');

        if(!IsNullOrEmpty(field.getSelectedRecord()))
        {
            btnClear.setVisible(true);
        }
    },

    onCmbNameSelect: function(combo, records, eOpts) {
        var form = this;

        var grdProducts = FindControl(form, 'grdProducts');
        grdProducts.getStore().clearExtraParams();
        grdProducts.getStore().removeAll();
        grdProducts.getStore().addExtraParams({
            filter: {
                Name: combo.getSelectedRecord().get('Name')
            }
        });

        grdProducts.getStore().loadPage(1);
    },

    onCmbNameAfterRender: function(component, eOpts) {
        var form = this;

        var grdProducts = FindControl(form, 'grdProducts');

        grdProducts.getStore().clearExtraParams();
        grdProducts.getStore().removeAll();

        grdProducts.getStore().loadPage(1);
    },

    onBtnClearClick: function(button, e, eOpts) {
        var form = this;

        var cmbName = FindControl(form, 'cmbName');
        cmbName.reset();

        button.setVisible(false);

        var grdProducts = FindControl(form, 'grdProducts');

        grdProducts.getStore().clearExtraParams();
        grdProducts.getStore().removeAll();

        grdProducts.getStore().loadPage(1);
    }

